mapper.select:
SELECT COUNT(eno) 
FROM event

	AND eno = #{eno}
	AND etitle like '%'||#{etitle}||'%'
	AND econtent like '%'||#{econtent}||'%'
	AND estatus = #{estatus}
	AND to_char(eissuedate, 'YYYY-MM-DD') >= #{eissuedate}
	AND to_char(eexpiredate, 'YYYY-MM-DD') <= #{eexpiredate}

SELECT eno, etitle, econtent, eissuedate, eexpiredate, elimitcount, ecount, eimg ,ediscount, estatus, edetailimg, ecoupontitle
FROM event
WHERE eno = #{eno}

mapper.update:
UPDATE event
etitle=#{etitle}, econtent=#{econtent}, eissuedate=#{eissuedate}, eexpiredate=#{eexpiredate}, eimg=#{eimg}, edetailimg=#{edetailimg}, ecoupontitle=#{ecoupontitle}, elimitcount=#{elimitcount}, ediscount=#{ediscount}, estatus=#{estatus},
WHERE eno = #{eno}
