mapper.select:
SELECT COUNT(mid) 
FROM member

	AND menabled = #{menabled}
	AND mid like '%'||#{mid}||'%'
	AND mname like '%'||#{mname}||'%'
	AND mphone like '%'||#{mphone}||'%'
	AND mgrade = #{mgrade}
	AND mgender = #{mgender}

SELECT mid, mname, memail, mphone, mtel, mzipcode, maddress1, maddress2, mbirth, mgender, mrefid, mrole, mgrade, mmileage,menabled
FROM member
WHERE mid=#{mid}

mapper.update:
UPDATE member
mname=#{mname}, memail=#{memail}, mphone=#{mphone}, mtel=#{mtel}, mzipcode=#{mzipcode}, maddress1=#{maddress1}, maddress2=#{maddress2},
WHERE mid = #{mid}

UPDATE member
SET menabled=1
WHERE mid=#{mid}
